INSERT INTO `list_films` (`id_film`, `name_orig`, `name_ru`, `film_year`, `creater`, `actors`, `descript`, `poster`, `film_time`, `disk`, `ganre`, `film_country`) VALUES
(1, 'Avatar', 'Аватар «Это новый мир»', '2009', 'Джеймс Кэмерон', 'Сэм Уортингтон, Зои Салдана, Сигурни Уивер, \r\n\r\nСтивен Лэнг, Мишель Родригес', 'Джейк Салли — бывший морской пехотинец, прикованный к \r\n\r\nинвалидному креслу. Несмотря на немощное тело, Джейк \r\n\r\nв душе по-прежнему остается воином. Он получает задание \r\n\r\nсовершить путешествие в несколько световых лет к базе \r\n\r\nземлян на планете Пандора, где корпорации добывают \r\n\r\nредкий минерал, имеющий огромное значение для выхода \r\n\r\nЗемли из энергетического кризиса.', 'face_Avatar_2009.jpg', '02:34:31', 'Films001', 'фантастика, боевик, триллер, приключения', 'США, Twentieth Century-Fox Film Corporation'),
(2, 'Up in the Air', 'Мне бы в небо', '2009', 'Джейсон Рейтман', 'Джордж Клуни, Вера Фармига, Анна Кендрик, Джейсон Бейтман, Эми Мортон, Мелани Лински, Дж.К. Симмонс, Сэм Эллиотт, Дэнни МакБрайд, Зак Галифианакис', 'Райан Бинэм — корпоративный юрист, всё время проводящий в командировках. Образцовый потребитель, он преуспел в корпоративных связях, чем не может похвалиться в личной жизни. Отношения на одну ночь для него просто часть длинного списка с бесчисленными люксами отелей, арендованными автомобилями, электронной почтой и сообщениями на автоответчике. Постоянные разъезды отдалили его от родной семьи, а свою он так и не создал. Порой Райану кажется, что в небе существует отдельное государство. Пункт регистрации в аэропорту в нём граница, питание на борту — местная кухня, премиальные мили — валюта. Да, он налетал много миль, до рекордного одного миллиона ему осталось совсем немного. Накопление и подсчёт премиальных миль очень занимают Райана, ведь он считает их своей неотъемлемой частной собственностью, способной осуществить его мечты. Тем очевидней окажется бессмысленность его надежд, когда компания, управляющая начислением иллюзорных бонусов начнёт менять правила игры.', 'face_Mne_by_v_nebo_2009.jpg', '01:49:19', 'Films001', 'комедия, мелодрама, драма', 'США, Paramount Pictures'),
(3, 'Sherlock Holmes', 'Шерлок Холмс «От них не скроешься»', '2009', 'Гай Ричи', 'Роберт Дауни мл., Джуд Лоу, Рэйчел МакАдамс, Марк Стронг, Эдди Марсан, Роберт Мэйллет, Джеральдин Джеймс, Келли Райлли, Уильям Хьюстон, Хэнс Мэтисон', 'Величайший в истории сыщик Шерлок Холмс вместе со своим верным соратником Ватсоном вступают в схватку, требующую нешуточной физической и умственной подготовки, ведь их враг представляет угрозу для всего Лондона.', 'face_Sherlok_Holms_2009.jpg', '02:02:16', 'Films001', 'боевик, приключения, детектив', 'США, Warner Bros. Pictures'),
(4, 'A Dangerous Man', 'Опасный человек', '2010', 'Киони Ваксман / Keoni Waxman', 'Стивен Сигал / Steven Seagal - Shane Daniels Марлэйна Мах / Marlaina Mah - Tia Виталий Кравченко / Vitaly Kravchenko - Vlad Байрон Манн / Byron Mann - The Colonel Джесси Хатч / Jesse Hutch - Sergey Майк Допуд / Mike Dopud - Clark Джерри Вассерман / Jerry Wasserman - Ritchie Байрон Лоусон / Byron Lawson - Mao Дэрил Шаттлворф / Daryl Shuttleworth - Detective Винсент Чэн / Vincent Cheng - Uncle Kuan Александр Мандра / Alexander Mandra - Russian thug', 'Шэйн Дэниэлс, несправедливо осужденный за преступление, которого не совершал, садится в тюрьму, дабы отбыть положенный законодательством срок. По выходе из тюремных стен он осознаёт, что жизнь на улицах… ничуть не изменилась — толкающие наркоту, торговцы оружием, гангстеры — поднаготная и тёмная сторона этого мира встретили его во всей своей красе. Однако Шейн вовсе не собирается мириться с существующим порядком вещей, и объявляет войну преступникам, особенно, когда те пересекают его личные интересы.\r\n[br][br]\r\nВ фильме присутствует ненормативная лексика и множественные сцены насилия. Оградите детей от просмотра фильма.!!!', 'face_A_Dangerous_Man_2010.JPG', '01:30:32', 'Films001', 'боевик, триллер', 'США'),
(5, 'The Fourth Kind', 'Четвертый вид', '2009', 'Олатунде Осунсанми', 'Милла Йовович, Уилл Пэттон, Хаким Кае-Казим, Кори Джонсон, Энзо Силенти, Элиас Котеас, Эрик Лорен, Мия МакКенна-Брюс, Рафаэль Коулмэн, Дафна Александр', 'Триллер основанный на реальных событиях, рассказывающий о таинственных исчезновениях на Аляске в течение последних 40 лет.', 'face_cetvertyy_vid.JPG', '01:35:04', 'Films002', 'ужасы, фантастика, триллер, детектив', 'США'),
(6, 'The Lovely Bones', 'Милые кости «История жизни и всего, что следует за ней...»', '2009', 'Питер Джексон', 'Сирша Ронан, Марк Уолберг, Рейчел Вайс, Стенли Туччи, Сьюзен Сарандон, Аманда Мичалка, Джэйк Абель, Роуз МакИвер, Майкл Империоли, Никки СуХу', 'Фильм рассказывает о переживаниях четырнадцатилетней девушки, изнасилованной и убитой маньяком-соседом. Наблюдая за жизнью домочадцев с небес, героиня мечтает о простых подростковых радостях и жаждет поимки душегуба.[br]\r\n[br]\r\nсценарий Фрэнсис Уолш, Филиппа Бойенс, Питер Джексон[br]\r\n[br]\r\nпродюсер Марк Эштон, Филиппа Бойенс, Энн Брюнинг[br]\r\n[br]\r\nоператор Эндрю Лесни[br]\r\n[br]\r\nкомпозитор Брайан Ино[br]', 'face_milye_kosti.JPG', '02:01:00', 'Films002', 'триллер, фэнтези, драма, криминал', 'США, Великобритания, Новая Зеландия'),
(7, 'Sorority Row', 'Крик в общаге «Подруги по жизни... и смерти.»', '2009', 'Стюарт Хендлер / Stewart Hendler', 'Тери Анджеевски, Адам Берри, Меган Вулфли, Роберт Белуши, Мари Бланчард, Брайана Эвиган, Зак Гаррет, Марго Харшман, Румер Уиллис, Джэми Чунг', 'Группа студенток решает отомстить парню, изменяющему их подружке. Для этого они инсценируют смерть одной из любовниц студента-ловеласа. Юноша, который не знает, что все это лишь сомнительная шутка, убивает девушку уже по-настоящему. А уже на выпускном вдруг появляется таинственная фигура в балахоне. Этот кто-то знает про скрываемое студентами убийство и начинает мстить, уничтожая всех соучастников преступления.\r\n[br]\r\n[br]\r\n\r\nДоп. информация:\r\n[br]\r\n\r\n[br]\r\n\r\nсценарий Джош Столберг, Пит Голдфингер, Марк Росман\r\n[br]\r\nпродюсер Билл Баннерман, Jay Boberg, Дуглас Кертис, ...\r\n[br]\r\nоператор Кен Сенг\r\n[br]\r\nкомпозитор Люсиан Пьяне\r\n[br]\r\nбюджет $12 500 000\r\n[br]\r\nсборы в США $11 965 282\r\n[br]\r\nсборы в мире + $4 371 708 = $16 336 990\r\n[br]\r\nсборы в России $688 873\r\n[br]\r\nпремьера (мир) 9 сентября 2009\r\n[br]\r\nпремьера (РФ) 4 ноября 2009, «West»[br]', 'face_krik_v_obshage.jpg', '01:40:58', 'Films002', 'ужасы, триллер', 'США'),
(8, 'Daybreakers', 'Воины света', '2009', 'Майкл Спириг, Питер Спириг', 'Этан Хоук, Уиллем Дефо, Сэм Нил, Джей Лагаая, Дэмиен Гарви, Аллан Тодд, Питер Велман, Мишель Эткинсон, Джек Брэдфорд, Крис Браун', '2019 год. Глобальная эпидемия превратила большинство людей в вампиров, поставив человечество на грань вымирания. Безмятежное будущее не светит, впрочем, и кровососам: в условиях дефицита крови они вынуждены холить и лелеять каждую оставшуюся в живых человеческую особь — либо синтезировать заменитель крови. Противостоять концу света пытается группа ученых, ищущая средства для борьбы с вампирами. ', 'face_VoinySvata.jpg', '01:29:05', 'Films003', 'ужасы, фантастика, боевик, триллер, драма', 'США / Австралия, Lionsgate'),
(9, 'The Road', 'Дорога', '2009', 'Джон Хиллкоут / John Hillcoat', 'Вигго Мортенсен/Viggo Mortensen, Шарлиз Терон/Charlize Theron, Роберт Дювалл/Robert Duvall, Гай Пирс/Guy Pearce, Молли Паркер/Molly Parker', 'На Землю обрушились чудовищные катаклизмы, цивилизация уничтожена, как и практически вся жизнь на планете. Оставшееся человечество разделилось на каннибалов и их добычу. По дороге, покрытой пеплом, идут отец с сыном. Они хотят добраться до теплых мест, чтобы выжить.\r\n\r\n', 'face_The_Road.jpg', '01:27:01', 'Films003', 'фантастика, триллер, драма', 'США'),
(10, 'Armored', 'Инкассатор', '2009', 'Нимрод Антал', 'Мэтт Диллон, Жан Рено, Лоренс Фишбёрн, Коламбус Шорт, Амори Ноласко, Фред Уорд, Майло Вентимилья, Скит Ульрих, Андре Кинни, Эндрю Фисцелла', 'Охранник-инкассатор поддается на уговоры своих товарищей и предпринимает попытку угнать бронированный автомобиль с 42 миллионами долларов.', 'face_Inkasator.jpg', '01:27:51', 'Films003', 'боевик, триллер, драма', 'США'),
(11, 'Joao Magueijo', 'Большой взрыв Жоао Магейжо', '2008', 'Жоао Магейжо', 'Жоао Магейжо', 'Жоао Магейжо:\r\n\r\nЯ космолог.\r\n\r\nМоя работа, моя жизнь - узнать, почему всё происходит так, а не иначе. Почти 20 лет я изучаю Вселенную. Из-за моих идей меня называют анархистом, еретиком, радикалом и даже дураком.\r\n\r\nДобро пожаловать в сумасшедший мир!\r\n\r\nА я знаю, что произошло а самом начале.....', 'ea563dbd0767de9be13504da68b92873.jpg', '00:44:09', 'Films003', 'документальный, Научно-популярный', 'USA'),
(12, 'The Universe: Beyond the Big Bang', 'Вселенная: По ту сторону Большого Взрыва', '2007', '', '', 'Порядка 13,7 млрд лет назад нечто загадочное запустило Вселенную, произведя Большой Взрыв - первооснову сотворения любого атома, любой звезды, любой галактики. Об этом наш рассказ. Как на протяжении тысячелетий мы складывали мозаичную картину Вселенной, искали научную логику, выясняя свое место в этом мире. Это рассказ обо всем...', 'face_beyond_the_Big_Bang.jpg', '01:30:05', 'Films003', 'документальный', 'USA Производство AETNInternational'),
(13, 'Naked Science: Big Bang', 'С точки зрения науки: Большой Взрыв', '2009', 'Мелиса Акдоган', '', '"С точки зрения науки" — это захватывающий сериал, который расскажет вам осе о самых популярных у актуальных научных темах нашего времени. Каждый эпизод, основываясь на последних достижениях научно-технического прогресса о мнениях передовых исследователей, доступным языком расскажет величайших загадкам нашей планете и за ее пределами. Все от исторических фактов до сверхъестественных событий, от природных аномалий до глобальных катастроф. Вы сможете не просто получить развернутые ответы ученых, но и с помощью современных спецэффектов как бы оказаться па месте событии. Добро пожаловать в мир знаний и удивительных открытий!', '', '00:46:51', 'Films003', 'Документальный', 'Pioneer Productions (for National Geographic Channel)'),
(14, 'Star Trek', 'Звездный путь', '2009', 'Джей Джей Абрамс / J.J. Abrams', 'Крис Пайн, Закари Куинто, Леонард Нимой, Эрик Бана, Брюс Гринвуд, Карл Урбан, Зои Салдана, Саймон Пегг, Джон Чо, Антон Ельчин', 'Судьба галактики в руках непримиримых соперников, принадлежащих к совершенно разным мирам. Один из них — Джеймс Т. Кирк, хулиган из Айовы, прирожденный лидер, и искатель приключений. Второй, Спок, вырос на планете Вулкан и стал изгоем по причине своего получеловеческого происхождения.\r\n\r\nКирк и Спок являются полными противоположностями; стремясь познать свою истинную сущность и понять, что они могут дать миру, молодые люди вскоре становятся непримиримыми соперниками. Один из них движим страстями, другой руководствуется холодной логикой.', 'face_Zvezdnyi_put.jpg', '02:01:33', 'Films004', 'фантастика, боевик, приключения', 'Германия, США'),
(15, 'Surrogates', 'Суррогаты «Реален только ты»', '2009', 'Джонатан Мостоу / Jonathan Mostow', 'Брюс Уиллис, Рада Митчел, Розамунд Пайк, Борис Коджо, Джеймс Джинти, Джеймс Кромуэлл, Винг Рэймс, Джек Ноузуорти, Девин Рэтрей, Майкл Кудиц', 'Действие разворачивается в будущем, в 2057 году, - когда люди практически перестанут общаться между собой и полностью возложат эту функцию на плечи роботов-заменителей. Они сильнее, моложе, привлекательнее своих обладателей и даже могут быть другого пола. Но находится террорист, начинающий уничтожать идеальных андроидов. Полицейскому Харви Гриру предстоит узнать, какие причины движут злоумышленником, лишающим жизни людей и их марионеток, технически и физически совершенных.', 'face_Surrogates.jpeg', '01:24:57', 'Films004', 'фантастика, боевик, триллер', 'США'),
(16, 'Love Happens', 'Любовь случается', '2009', 'Брэндон Кэмп / Brandon Camp', 'Дженнифер Энистон, Аарон Экхарт, Джуди Грир, Саша Александер, Мартин Шин, Дэн Фоглер, Джо Андерсон, Джина Холден, Брэндон Джей МакЛарен, Джон Кэрролл Линч, Рекха Шарма, Мишель Харрисон, Алессандро Джулиани, Энн Мари Лодер, Элли Харви, Майкл Копса, Дэниэл Бэйкон, Тайлер МакКлендон', 'Смерть жены стала для Берка Райана не только личной трагедией, но и, как бы кощунственно это ни звучало, удачным бизнесом. Вдовец написал книгу о том, как его вторая половинка медленно и мучительно угасала от смертельной болезни, и эта книга стала большим национальным бестселлером. Теперь Берк путешествует по стране и встречается с читателями, получая от них тонны сочувствия и признательности. Жизнь вроде бы удалась, рана на сердце постепенно залечивается, но от пустоты в душе избавиться очень трудно. И вот однажды Берк встречает Элоизу, красивую и независимую женщину, готовую изменить его жизнь и навсегда в ней остаться. Но для этого популярный вдовец должен изменить себя и стать совершенно новым человеком...', 'face_Ljubov_sluchaetsja.jpg', '01:49:09', 'Films004', 'Мелодрама', 'США'),
(17, 'The Time Traveler s Wife', 'Жена путешественника во времени', '2009', 'Роберт Швентке / Robert Schwentke', 'Мишель Нолден, Алекс Феррис, Арлисс Ховард, Эрик Бана, Барт Бедфорд, Эстер Джан, Мэтт Бирман, Рэйчел МакАдамс', 'Они познакомились, когда ей было шесть, а ему — тридцать шесть. Они поженились, когда ей было двадцать три, а ему тридцать один. Потому что Генри страдает редким генетическим заболеванием — синдромом перемещения во времени; его исчезновения из жизни Клэр непредсказуемы, появления — комичны, травматичны и трагичны одновременно.', 'poster_TheTimeTravel-s Wife_2009.jpg', '01:47:25', 'Films005', 'фантастика, мелодрама, драма', 'США'),
(18, 'Un prophete', 'Пророк', '2009', 'Жак Одиар / Zhac Odiar', 'Алаа Омузун, Нильс Ареструп, Жилль Коэн, Адель Беншериф, Тахар Рахим, Салем Гуермат, Соня Хелл, Жан-Филипп Риччи', 'Приговоренный к шести годам тюрьмы, Малик Эль Джебена кажется на фоне других заключенных самым «зеленым» и самым уязвимым. Ему 19 лет. Он не умеет ни писать, ни читать. За стенами тюрьмы у него нет близких.\r\n\r\n[br][br]\r\nВсе меняется, когда Лучано, лидер авторитетной банды корсиканцев, решает поручить Малику ряд опасных и рискованных миссий… Выполнение любой из этих «миссий» под прессингом корсиканцев могло бы сломить кого угодно, но не его. Каждое задание закаляет Малика. Он быстро учится жизни в новом для себя мире. Завоевывает доверие корсиканцев и мусульман. И вскоре начинает вынашивать собственные дерзкие планы…', 'face_Prorok.jpg', '02:28:48', 'Films005', 'Драма, криминал', 'Франция, Италия'),
(19, 'The Rebound', 'Нянька по вызову', '2009', 'Барт Фрейндлих', 'Кэтрин Зета-Джонс, Джастин Барта, Стив Антонуччи, Марк Алан Остин, Пол Басиле, Роберт Бизик, Эндрю Брэдли, Дэниэл Барресс, Меган Бирн, Элана Кадиз', 'У 25-летнего юноши начинается роман с матерью-одиночкой, живущей по соседству. Основной преградой в их отношениях становится то, что главная героиня существенно старше своего избранника', 'face_Njanka_po_vyzovu.png', '01:34:44', 'Films005', 'Комедия', 'США'),
(20, 'Static ( Glitch )', 'Нанотехнологии: Эволюция человека', '2008', 'Рэнди Додлин', 'Стив Байерс, Мори Чайкин, Даррион Лучио, Мэтью Эдисон, Дарелл Хикс, Кэтлин Робертсон\r\n\r\n', 'Троим хакерам приходит в голову гениальная идея - разработать микрочип, который заменит сотовый телефон. Эта новинка на основе нанотехнологий должна вживляться под кожу и передавать сигналы сообщений напрямую в мозг. Не имея возможности протестировать изобретение в лабораторных условиях, друзья решают проверить его работу на себе. Но после первого же теста у всей троицы открываются сверхъестественные способности...', 'face_Static(Glitch).jpg', '01:27:04', 'Films006', 'Фантастика', 'США, RHI Entertainment'),
(21, 'Masters of the Universe', 'Властелины вселенной', '1987', 'Гари Годдард /Gary Goddard', 'Дольф Лундгрен /Dolph Lundgren, Фрэнк Ланджелла /Frank Langella, Мег Фостер /Meg Foster, Кортни Кокс /Courteney Cox, Джеймс Толкан /James Tolkan, Челси Филд /Chelsea Field', 'Некогда прекрасная планета Этерния лежит в развалинах, став жертвой демонического Скелета и его подлых прихлебателей. Герой Этернии Химэн и его сторонники бегут на планету странных существ и обычаев. Это Земля, следущее поле боя со Скелетом и его пособниками!', 'face_MastersOfTheUniverse.jpg', '1:45', 'Films006', 'Фантастика, Боевик', 'США / Cannon Group'),
(22, 'Где находится Нофелет?', '', '1987', 'Геральд Бежанов', 'Владимир Меньшов, Александр Панкратов-Черный, Валентина Теличкина, Людмила Шагалова, Николай Парфенов, Марина Дюжева, Людмила Нильская, Наталья Коновалова, Ольга Шорина, Елена Покатилова, Елена Сафонова, Екатерина Жемчужная, Ирина Розанова, Ольга Кабо\r\n\r\n', 'Добряк и весельчак Генка, приехав в Москву по поручению жены покупать мебель, остановился у двоюродного братца Паши Голикова. Увидев, что сорокалетний Паша по-прежнему холост и одинок, Генка решает найти ему невесту...\r\n[br]\r\nГуляя по улицам, они стали знакомиться с девушками очень оригинальным способом: "Где находится нофелет?", -задают они женщинам один и тот же вопрос. Познакомиться при помощи "нофелета" удавалось, конечно же, не всегда, но некоторые милые женщины устоять не могли. Как только отношения заходили достаточно далеко, скромный Паша удирал домой, а Геннадию приходилось "оттдуваться" за двоих. Каждый день по дороге на работу, в транспорте Паша встречался с незнакомкой, в которую уже успел влюбиться. Но нерешительный влюбленный до сих пор не осмеливался с ней заговорить. И только своеобразный треннинг брата Генки помог Павлу взять себя в руки и... познакомиться-таки с женщиной своей мечты. [br]', 'face_GdeNahoditsyaNofelet.jpg', '01:18', 'Films006', 'Комедия', 'СССР'),
(23, 'Nine «This Holiday Season, Be Italian»', 'Девять', '2009', 'Роб Маршалл', 'Дэниэл Дэй-Льюис, Марион Котийяр, Пенелопа Крус, Николь Кидман, Джуди Денч, Кейт Хадсон, Софи Лорен, Стэйси Фергюсон, Рикки Тоньяцци\r\n\r\n', 'Известный режиссер Гвидо Контини должен закончить свой новый фильм. Но помимо этой проблемы есть и другая — его личная жизнь, поскольку он состоит в непростых отношениях с женой, любовницей, музой, агентом и еще с матерью. ', 'face_Devyat.jpg', '01:52:42', 'Films006', 'мелодрама, драма, мюзикл', 'США / Италия, The Weinstein Company'),
(24, 'Surveillance', 'Наблюдение', '2008', 'Дженнифер Чэмберс Линч / Jennifer Chambers Lynch', 'Кэролайн Аарон /Caroline Aaron/, Хью Диллон /Hugh Dillon/, David Gane, Гилл Гэйл /Gill Gayle/, Кент Харпер /Kent Harper/, Майкл Айронсайд /Michael Ironside/, Пелл Джеймс /Pell James/, Дэрил Хейни /Daryl Haney/, Шеннон Джардин /Shannon Jardine/, Angela Lamarsh, Gerald Lenton-Young, Дженнифер Майлз /Jennifer Miles/, Мак Миллер /Mac Miller/, Чарли Ньюмарк /Charlie Newmark/, Джулия Ормонд /Julia Ormond/, Шери Отери /Cheri Oteri/, Билл Пуллман /Bill Pullman/, Райан Симпкинс /Ryan Simpkins/, Анита Смит /Anita Smith/, Френч Стюарт /French Stewart/, Джош Стрейт /Josh Strait/, Kent Wolkowski /Kent Wolkowski/, Kyle Briere', 'Агент ФБР гоняется за серийным убийцей, ловя его на приманку: трех его потенциальных жертв, причем у каждого, разумеется, есть своя история.', 'Films007_poster_Nabljudenie.jpg', '01:37:28', 'Films007', 'Триллер, Детектив, Драма, Криминал', 'Германия, США'),
(25, 'Asterix & Obelix: Mission Cleopatre', 'Астерикс и Обеликс: Миссия Клеопатра', '2002', 'Ален Шаба / Alain Chabat', 'Obelix - Жерар Депардье\r\n,\r\nAsterix - Кристиан Клавье\r\n,\r\nNumerobis / Edifis - Жамель Деббуз\r\n, \r\nCleopatre - Моника Беллуччи\r\n,\r\nJules Cesar - Ален Шаба\r\n,\r\nPanoramix / Getafix - Клод Риш\r\n,\r\nGoblinus / Amonbofis - Жерар Дармон,\r\nOtis - Эдуард', 'Великая Клеопатра заключила пари с римским императором Юлием Цезарем о том, что ее подданные смогут всего за три месяца построить грандиозный дворец, превосходящий лучшие римские дворцы. Этой опасной чести был удостоен архитектор Нумернобис, которого — в случае неудачи — должны съесть крокодилы.\r\n\r\nНумернобис вызывает на помощь своего старого друга — волшебника-друида Панарамикса с зельем, дарующим сверхъестественную силу. А веселые, находчивые Астерикс и Обеликс вместе с песиком Идеафиксом сопровождают друида в Египет, чтобы помешать проискам Цезаря, не желающего проигрывать Клеопатре.', 'Films007_poster_asterix&obelix_mc.jpg', '1:43:34', 'Films007', 'фэнтези, комедия, приключения, семейный', 'Германия, Франция'),
(26, 'Seven Pounds «Seven Names. Seven Strangers. One Secret»', 'Семь жизней «Они не знакомы. У них разные судьбы. Но у них одна тайна»', '2008', 'Габриэле Муччино / Gabriele Muccino', 'Уилл Смит, Розарио Доусон, Вуди Харрельсон, Барри Пеппер, Эльпидия Каррильо, Мэдисон Петтис, Майкл Или, Йениффер Беренс, Лидиа А. Батлер, Джеффри Гулд, Джина Хехт, Тим Келлехер, Робинн Ли, Сара Джейн Моррис, Бояна Новакович, Джо Нуньес, Дэйл Рауль, Билл Смитрович, Стив Том', 'На первый взгляд Бен (Уилл Смит) может показаться обычным агентом налоговой службы США, который находит должников, сообщает им о задолженности и портит тем самым людям жизнь. Но первое мнение часто бывает ошибочным. Вовсе не долги его «клиентов» волнуют Бена больше всего, а их жизни, то, как люди их прожили. Бену нужно знать заслуживает ли человек права называться «хорошим», поскольку это может спасти недобросовестному налогоплательщику жизнь. Но пока 7 человек не знают, что их жизни находятся в руках одного человека. Они тщетно пытаются понять действия странного «налоговика», они лишь могут догадываться, что на душе у Бена лежит тяжкое бремя какой-то личной трагедии, которая изменила жизнь Бена, а теперь изменит и их жизни….', 'Films007_poster_Sem_zhiznej_2008.jpg', '02:03:05', 'Films007', 'драма', 'США    «Columbia\\Sony»'),
(27, 'Doomsday [Unrated]', 'Судный день', '2008', 'Нил Маршалл / Neil Marshall', 'Рона Митра /Rhona Mitra/, Adeola Ariyo, Эмма Клисби /Emma Cleasby/, Кристин Томлинсон /Christine Tomlinson/, Vernon Willemse, Paul Hyett, Дэниэл Рид /Daniel Read/, Karl Thaning /Karl Thaning/, Стефен Хьюз /Stephen Hughes/, Джейсон Коуп /Jason Cope/, Райан Крюгер /Ryan Kruger/, Натан Уитли /Nathan Wheatley/, Сесил Картер /Cecil Carter/, Джереми Кратчли /Jeremy Crutchley/, Кэрин Петерсон /Caryn Peterson/, Tom Fairfoot, Элуиз Купидо /Eloise Cupido/, Лили Андерсон /Lily Anderson/, Az Abrahams, Боб Хоскинс /Bob Hoskins/, Александр Сиддинг /Alexander Siddig/, Дэвид О''Хара /David O''Hara/, Джон Фалкоу /Cokey Falkow/, Джон Карсон /John Carson/, Натали Болтт /Nathalie Boltt/, Сьюзэн Дэнфорд /Susan Danford/, Адриан Лестер /Adrian Lester/, Рик Уорден /Rick Warden/, Нора-Джейн Нун /Nora-Jane Noone/, Лесли Симпсон /Leslie Simpson/', 'Великобритания, недалёкое будущее. Смертельный и совершенно неизученный вирус, названный "Жнецом" ("Reaper Virus") обрушился на страну, убивая десятки и сотни тысяч жителей. Не имея возможности решить проблему, правительство решает изолировать очаг заразы от не инфицированной местности, построив вокруг территории буйства "Жнеца" стену.\r\n[br]\r\nПроходит 30 лет, но вирус снова заявил о себе. Тогда было решено послать в заражённую область первоклассную команду спецназа для поисков возможных методов борьбы с вирусом. Ради выполнения цели и сохранения жизней ребятам придется пробиваться через настоящий оживший кошмар…', 'Films008_poster_Sudniy_den_2008.jpg', '01:52:50', 'Films008', 'Фантастика, Боевик, Триллер', 'Германия, США, Великобритания, ЮАР'),
(28, 'Over Her Dead Body', 'Невеста с того света', '2008', 'Джефф Лоуелл', 'Ева Лонгория, Пол Радд, Лэйк Белл, Джейсон Биггз, Линдсэй Слоун, Стивен Рут, Уильям Морган Шеппард, Венди МакЛендон-Кови, Али Хиллис, Дебора Тикер', 'Рядом с ней любимый человек, она счастлива, жизнь прекрасна! Но иногда романтические отношения омрачаются «тенью прошлых романов». В этой истории все так и случилось, в прямом смысле этого слова: к влюбленной девушке является дух бывшей подруги ее парня и начинает мучить обстоятельными советами, вынуждая тем самым постоянно оказываться в конфузных ситуациях…', 'Films008_poster_Nevesta_s_togo_sveta_2008.jpg', '1:30:31', 'Films008', 'фэнтези, комедия, мелодрама', 'США'),
(29, 'The Children of Huang Shi', 'Дети Хуанг Ши', '2008', 'Роджер Споттисвуд / Roger Spottiswoode', 'Джонатан Рис-Мейерс, Рада Митчелл, Чоу Юн-Фат, Мишель Йео, Гуан Ли, Мэтт Уокер, Анастасия Колпакова, Пинг Су, Имаи Хидэаки, Сеихиро Хасимото.', 'Молодой английский журналист Джордж Хогг и австралийская медсестра Ли Пирсон должны спасти от смерти группу осиротевших детей во время японского вторжения в Китай в 1937 году. Задача главных героев - переправить 60 ребятишек через горы Лю Пан Шан в безопасную местность...', 'Films008_poster_Deti_Huang_Shi_2008.jpg', '02:04:00', 'Films008', 'Историческая драма', 'Германия, Австралия, Китай'),
(30, 'Vantage Point', 'Точка обстрела', '2008', 'Пит Трэвис', 'Форест Уитакер (Говард Льюис), Деннис Куэйд (Агент Томас Барнс), Мэттью Фокс (Агент Кент Тэйлор), Сигурни Уивер (Рекс Брукс), Уильям Херт (Президент Эштон), Эдгар Рамирес (Хавьер), Зои Салдана, Ричард Т. Джонс, Эдуардо Норьега (Энрике), Леонардо Нам (Кевин Гросс), Саид Тагмауи (Суарез), Шелби Феннер (Грейс Риггз), Долорес Хередиа, Росио Вердехо, Лиза Оуэн, Родриго Качеро, Джастин Сундквист (Парсон), Хосе Карлос Родригес, Ари Брикман, Айэлет Цурер', 'Во время публичного выступления на антитеррористическом форуме в Саламанке, Испания от рук экстремистов погибает президент США — так, по-крайней мере, событие выглядит на первый взгляд. Вслед за этим мощный взрыв убивает сотни людей. Случившееся показано с разных точки зрения: сотрудников спецслужб, случайных прохожих, террориста-фанатика...[br]\r\nТолько проследив за происходящим через призму восприятия каждого из этих людей, можно приблизиться к пониманию истиной сути событий, связанных с покушением.[br]', 'poster_Tochka_obstrela_2008.jpg', '1:30:05', 'Films009', 'триллер, драма', 'США'),
(31, '[?REC]', 'Репортаж «Прямая трансляция из ада»', '2007', 'Хаум Балагуэро, Пако Плаза', 'Мануэла Веласко, Ферран Терраза, Пабло Россо, Хавьер Боте, Мануэль Брончад, Марта Карбонелл, Клаудия Фонт, Винсент Джил, Мария Ланау, Карлос Ласарте', 'Телерепортер Анжела Видаль с оператором Пабло снимают репортаж о ночной смене пожарной бригады для передачи «Пока вы спите». Вместе с пожарным расчётом они прибывают по вызову к дому, где обнаруживают двух полицейских и около десятка жильцов, напуганных криками из одной из запертых комнат. Пожарные и полицейские вскрывают комнату и находят там полуодетую пожилую женщину в крови, которая ведёт себя странно и вдруг бросается на полицейского и сильно кусает его в шею. Раненого приносят на первый этаж, где толпятся жильцы, а спустя несколько минут туда же падает с лестничного пролёта пожарный, также со следами укуса. Оба раненых в тяжелейшем состоянии, однако вынести их из дома и выйти оттуда сами герои уже не могут: дом полностью блокирован прибывшими полицейскими, которые объявляют, что из-за опасности заражения все жители должны оставаться внутри и ожидать прибытия санитарного инспектора....', 'Films009_poster_Reportazh_2008.jpg', '1:15:14', 'Films009', 'ужасы', 'Испания'),
(32, 'Last Hour', 'Последний час', '2008', 'Паскаль Кабуэт', 'Майкл Мэдсен, Дэвид Кэррадайн, Пол Сорвино, Моника Круз, Паскаль Кабуэт, Беттина Энтони, Квонг Люн Вонг, Тони Д`Амарио, Стивен Энг\r\n', 'Как бы вы отреагировали, если через 3 года после смерти вашего отца, вы получили бы от него письмо, в котором вас приглашают посетить заброшенный дом где-то в Китае? Любопытство берёт верх, и вы отправляетесь на приключение. Прибыв на место, вы обнаруживаете дом, больше похожий на бункер и четырёх конкурентов, с точно такими же письмами! Всех пятерых героев что-связывает. Все вооружены и разыскиваются полицией. Неожиданно появляется таинственная женщина, герои осознают что заперты внутри, дом окружён полицией, а жить им осталось всего-лишь один час!', 'Films009_poster_Poslednij_chas_2007.JPG', '01:34:46', 'Films009', 'Боевик, триллер, драма', 'Франция / Гонконг, Brand New Films / Gold Master'),
(33, 'Cloverfield', 'Монстро', '2008', 'Мэтт Ривз', 'Майкл Сталь-Дэвид, Одетт Юстман, Майк Фогель, Джессика Лукас, ТиДжей Миллер, Лиззи Каплан, Анжул Нигам, Марго Фарли, Тео Росси, Брайан Клагман', 'Прощальную вечеринку уезжающего в Японию Роба прерывает громогласный рык — нечто напало на Нью-Йорк, сея панику, разрушения и пожирая людей. Армия бессильна против гигантского монстра, Манхэттен объявлен закрытой зоной, но где-то неподалеку от Центрального парка в беду попала девушка Роба…', 'Films009_poster_monstro_2008.jpg', '01:24:41', 'Films009', 'Фантастика, триллер, детектив', 'США'),
(34, 'Луна. Секретная зона', 'Луна. Секретная зона', '', '', '', 'Тысячелетиями люди вглядывались в загадочный диск Луны. Гадали о происхождении спутника, мечтали ступить на поверхность, представляли себе лунных жителей. И вот наступила эпоха, когда мечты начали сбываться. Стараясь опередить друг друга, космические державы рванули к Луне. Облетели ее, сфотографировали невидимую сторону, с помощью автоматов доставили на Землю образцы лунного грунта. И вот наконец - момент истины: земляне ходят по поверхности желтой планеты. В планах - еще более захватывающие свершения: лунные поселения, стартовые площадки для освоения дальнего космоса.\r\nИ вдруг все проекты неожиданно сворачивают. Уже изготовленные ракеты, луноходы, научное оборудование остаются невостребованными.\r\n', 'Films009_poster_Luna_Secretnaya_zona_2007.jpg', '44 мин.', 'Films009', 'Фантастика, документальный', 'Россия'),
(35, 'Hot Fuzz', 'Типа крутые легавые «Будем мочить в сортире!»', '2007', 'Эдгар Райт / Edgar Wright', 'Саймон Пегг, Мартин Фриман, Билл Найи, Robert Popper, Джо Корниш, Крис Уэйт, Эрик Мэйсон, Билли Уайтлоу, Ник Фрост, Питер Уайт\r\n', 'Главный лондонский полицейский, констебль Николас Эйнджел переведен из города в тихий сельский городок Сэндфорд. Он пытается привыкнуть к этой мирной, и на первый взгляд лишенной преступности жизни, а также к туповатому напарнику Дэнни Баттерману. Однако, серия ужасных происшествий потрясает городок, заставляя Эйнджела убедиться в том, что Сэндфорд совсем не такой, каким кажется на первый взгляд. Мечты Дэнни о бурной, взрывоопасной, полной стрельбы и погонями жизни становятся все более и более реальными. Настало время для провинциальных полицейских вершить правосудие больших городов!', 'Films010_poster_Tipa_krutie_legavie_2007.jpeg', '02:00:52', 'Films010', 'боевик, комедия, детектив', 'Франция, Великобритания');
INSERT INTO `list_films` (`id_film`, `name_orig`, `name_ru`, `film_year`, `creater`, `actors`, `descript`, `poster`, `film_time`, `disk`, `ganre`, `film_country`) VALUES
(36, 'JUMPER', 'ТЕЛЕПОРТ', '2008', 'Даг Лиман', 'Хэйден Кристенсен, Сэмюэл Л. Джексон, Джейми Белл', 'Подросток из неблагополучного района Дэвид Райс всегда считал себя обычным парнем, пока однажды не узнал, что может телепортироваться с места на место. Новые способности открыли перед ним весь мир. Он может побывать в Нью-Йорке и Токио, посетить античные развалины в Риме, увидеть "крышу мира" с горы Эверест, увидеть 20 рассветов и 20 закатов. И все - в один день. Теперь ему не нужны деньги - он может взять сколько хочет. Однако в один момент он обнаруживает, что стал мишенью. На него, как и на других, подобных ему, объявлена охота. На протяжении тысячи лет тайное общество стремится уничтожить их. Вскоре Дэвид узнает истинную ценность его новых способностей.', 'Films010_poster_teleport_2008.jpg', '84 мин.', 'Films010', 'Боевик, Фантастика', 'США'),
(37, 'МЫ ИЗ БУДУЩЕГО', 'МЫ ИЗ БУДУЩЕГО', '2008', 'Андрей Малюков', 'Данила Козловский (Борман), Андрей Терентьев (Спирт), Владимир Яглыч (Череп), Дмитрий Волкострелов (Чуха), Екатерина Климова (Медсестра Нина Полякова), Борис Галкин (Старшина Емельянов), Даниил Страхов (Старший лейтенант Демин), Сергей Маховиков (Политрук Карпенко), Игорь Черневич (Жорин), Ральф Шиха (Оберст Бонхоф), Зоя Буряк (Клава), Сергей Мухин (Соколов)', 'Действие картины разворачивается в двух временных пластах: в наши дни и в годы войны. Главные герои ленты — четверо «черных копателей» — Борман, Череп, Чуха и Спирт. Они ведут раскопки в тех местах, где когда-то шли бои, чтобы потом продать найденные медали, ордена, документы и немецкое оружие. Однажды во время раскопок они находят солдатские книжки, принадлежавшие погибшим бойцам Красной армии, а в этих документах ребята обнаруживают ... свои собственные фотографии. Объясняя происходящее действием плохой водки, купленной в деревенском ларьке, коллеги по «черному» бизнесу бегут освежиться в озере и ... попадают в самый разгар тяжелых оборонительных боев в августе 1942-го', 'Films010_poster_My_iz_buduschego_2008.JPG', '01:55:35', 'Films010', 'Приключения, фантастика, война, драма', 'Россия'),
(38, 'Время ДДТ', 'Время ДДТ', '2002', 'Василий Бледнов', '', 'Четырехсерийный фильм о группе "ДДТ" снял режиссер Василий Бледнов. По задумке режиссера, в документально-музыкальной картине будет прослеживаться история нашей страны последних 20 лет через призму биографии "ДДТ" и ее лидера Юрия Шевчука.[br]\r\n[br]\r\n1 серия[br]\r\n"Периферия"[br]\r\n(1980 - 1985)[br]\r\nРассказывает о создании группы, уфимском периоде ДДТ, записи первых альбомов. Об участии и победе в конкурсе "Золотой камертон", преследованиях со стороны местных властей, о вынужденных отъездах в Череповец и Москву и об окончательном переезде в Ленинград.[br]\r\n[br]\r\n2 серия[br]\r\n"Ленинград"[br]\r\n(1986 - 1991)[br]\r\nРассказывает о приезде Юрия Шевчука в Ленинград, о его вхождении в питерское рок-движение, создании нового, питерского состава группы, сделавшего имя ДДТ известным среди миллионов поклонников рок-музыки, как раз в эти годы, годы перестройки, вышедшей из подвалов на стадионы и ставшей "властительницей дум".[br]\r\nНаглядным подтверждением этих изменений послужили события августа 1991-го, в конечном итоге приведшие к распаду империи СССР.[br]\r\n[br]\r\n3 серия[br]\r\n"Рожденный в СССР"[br]\r\n(1992 - 1995)[br]\r\nРассказывает о трагических событиях в жизни Юрия Шевчука, связанных со смертью его жены Эльмиры, и об альбоме "Актриса весна", посвященном ее памяти. О новом периоде в жизни группы, связанном с созданием знаменитых концертных программ: "Черный Пес Петербург", "Это все" и "Рожденный в СССР", в которых отразились размышления, вапечатления, боль этого времени, на которое пришлись очередные потрясения - октябрьский путч 1993-го и начало войны с Чечней.\r\nВ фильме используются уникальные съемки, сделанные самим Шевчуком во время его пребывания в Чечне во время первой чеченской кампании 1995-го года.[br]\r\n[br]\r\n- С этой серией недавно произошла интересная и очень важная для нас история, - делится режиссер Василий Бледнов. - Недавно его показали на канале ТВС, и после показа серии про Чечню (а там много любительских съемок, сделанных самим Шевчуком, очень сильный, бьющий наотмашь материал...) мне позвонил по телефону какой-то человек - кажется, из Мариуполя. Он долго и смущенно извинялся за звонок, а потом сказал: "У вас там в фильме Шевчук разговаривает с офицером о войне, и тот говорит - вот, мол, на хрена нам эта война, вон у меня в подвале сейчас два трупа лежат обгорелых... И назвал фамилии. Так вот, я - брат одного из этих погибших солдат. Я разыскиваю его с 1995 года..."[br]\r\nЭтот человек просил меня сообщить фамилию офицера, который был в кадре, когда и где это было снято... Я объяснил, что это частная съемка Шевчука и дал ему телефон студии ДДТ. Видимо, он узнал там что-то очень важное, потому что 9 мая домашние мне передали: звонил человек из Мариуполя, поздравлял с праздником и очень, очень благодарил... Так неожиданно еще раз "аукнулась" эта съемка получилось, что Юрий Шевчук не только поддерживал этих ребят своими песнями, самим своим присутствием, но и вот так, невольно, помог кому-то найти без вести пропавшего брата.[br]\r\n[br]\r\n4 серия[br]\r\n"Мама, это рок-н-ролл?"[br]\r\n(1996-2002)[br]\r\nСерия рассказывает о 1996-2002 годах в жизни группы, куда вошли: запись альбома "Любовь" на студии "Аэросмит" в США; поиск нового звучания, приведший к созданию программы "Мир Номер Ноль", которая открыла зрителю "новую" ДДТ; связанные с этим изменения в составе группы; выпуск альбома "Метель августа", на фоне заглавной композиции с которого звучат размышления музыкантов о роли ДДТ в их жизни и о планах на будущее.[br]\r\n[br]\r\nВ фильме в большом количестве используются уникальные материалы из личного архива Юрия Шевчука, музыкантов ДДТ и близких к группе людей.\r\nРежиссер Василий Бледнов:[br]\r\n- Сейчас мы совместно с братом (оператор Сергей Бледнов) пишем сценарий художественног музыкального фильма "Мир Номер Ноль" (антиутопия группы ДДТ) на культовый одноименный альбом группы. Это будет экранизация альбома "Мир Номер Ноль" в стиле легендарной "Стены" группы Pink Floid. Так что время ДДТ - продолжается!', 'Films011_poster_Vremya_DDT_2002.jpeg', '0:21:04', 'Films011', 'документально-музыкальный фильм', 'Россия'),
(39, 'Самый лучший фильм 2', 'Самый лучший фильм 2', '2009', 'Олег Фомин', 'Тимур Батрутдинов, Гарик Харламов, Дмитрий Хрусталев, Олег Верещагин, Михаил Галустян, Дмитрий Нагиев, Александр Баширов, Сергей Лазарев, Сергей Зверев, Екатерина Конисевич', 'Четверо закадычных друзей — Моряк, Актер, Мажор и Димати — после долгой разлуки встречаются в московском кафе отметить день рождения Мажора. На сей раз у них есть наличные, чтобы расплатиться с официанткой. И бегать по городу в поисках денег они будут совершенно по другой причине. Мажора похитили и требуют выкуп — миллион евро. Чем друзья будут расплачиваться с бандитами, кто найдет новую любовь, а вместе с ней и приключения на все органы жизнедеятельности, кому придется испытать все тяготы сложного пути шоу-бизнеса к популярности, каково рэпперу кавказской наружности гулять одному по безлюдным улицам?! Но все это впереди, а пока они молоды, красивы, полны сил, надежд и ждут приключений! ', 'Films011_poster_Samiy_luchshiy_film-2_2009.png', '01:21:36', 'Films011', 'Комедия', 'Россия'),
(40, 'Планеты', 'Планеты', '1999', '', '', 'Первые снимки планет . Есть ли жизнь на Марсе ? Столкновение планет и кризис . Желаю поучителного просмотра .', 'Films011_poster_Planety_2007.jpg', '0:21:04', 'Films011', 'публицистика', 'Россия'),
(41, 'Death Note', 'Тетрадь смерти', '2006', 'Араки Тэцуро', '', 'Что ты сделаешь, если к тебе в руки попадёт Тетрадь, записав в которую чьё-либо имя ты обрекаешь его на смерть? Как ты себя поведёшь, когда у тебя за спиной, вдруг, чей то голос скажет «Я - бог смерти»? Чьё имя ты запишешь в эту тетрадь первым своей рукой, как ты уготовишь ему его смерть? Лайт, умный многообещающий юноша, осознав всю власть и силу этой тетради, решает создать идеальный мир – без насилия, преступности и зла. Но какими путями он станет к этому идти, на всё ли он готов ради того чтобы стать богом нового мира? Что он станет делать, когда у него на пути встанут не менее сильные противники, не согласными с его методами – полиция, Л, его отец? Он ли владеет этой тетрадью или это тетрадь завладела им? ', 'poster_Death_Note_2006.jpg', '37 х ~ 00:22:50', 'Films012 (ep01-24)[br] Films013 (ep25-37)', 'Детектив, мистика, драма, психология, триллер, анимэ', 'Япония'),
(42, 'Secret defense', 'Секреты государства', '2008', 'Филипп Аим', 'Жерар Ланвен, Ваина Джоканте, Николя Дювошель, Мехди Неббу, Рашида Бракни, Саймон Абкарян', 'Во Франции террористические группы и разведка ведут битву в ежедневной безжалостной войне во имя радикально противоположных идеологий. Тем не менее, террористические и тайные агенты ведут почти одинаковый образ жизни. Алекс и Аль-Барад - два из них. Первый глава из DGSE, французский эквивалент ЦРУ или MI6 по борьбе с терроризмом, а второй руководит террористической сетью... ', 'poster_Secrety_Gosudarstva_2008.jpg', '01:36:44', 'Films013', 'триллер, боевик', 'Франция'),
(43, 'Princess of Mars', 'Принцесса Марса', '2009', 'Марк Аткинс', 'Антонио Сабато мл., Трейси Лордс, Мэтт Ласки, Chacko Vadaketh, Митчел Гордон, Noelle Perris, Ali Tagi Alexander, Джей Бейерс, Shahrooz Bigonah, Tiffany Borland', 'Джон Коннор — капитан армии Конфедеративных штатов парализован в битве за Аризону. Получив новое тело в секретной лаборатории, он по заданию командования телепортируется на Марс. Его миссия — спасти планету от беспощадной тирании Тарков и передать власть истинным правителям Марса… ', 'poster_Princessa_Marsa_2009.jpg', '01:39:55', 'Films013', 'фантастика', 'США'),
(44, 'The Green Mile', 'Зеленая миля', '1999', 'Frank Darabont (Фрэнк Дарабонт)', 'Tom Hanks (Том Хэнкс), David Morse (Дэвид Морз), Bonnie Hunt (Бонни Хант), Michael Clarke Duncan (Майкл Кларк Данкан), James Cromwell (Джеймс Кромвелл), Michael Jeter (Майкл Джетер)', 'По мотивам произведения Стивена Кинга "Зеленая Миля". Обвиненный в страшном преступлении, Джон Коффи (Майкл Кларк Дункан, "Армагеддон", "Девять ярдов", номинация на премии "Оскар" и "Золотой Глобус" за эту роль) оказывается в блоке смертников тюрьмы "Холодная гора". Вновь прибывший обладал поразительным ростом и был пугающе спокоен, что, впрочем, никак не влияло на отношение к нему начальника блока Пола Эджкомба (Том Хэнкс, "Спасти рядового Райана", "Форрест Гамп"), привыкшего исполнять приговор. Гигант удивил всех позже, когда выяснилось, что он обладает невероятной магической силой...[br]\r\nНаграды: четыре номинации на премию "Оскар", премия Американской академии кинофантастики и фильмов ужасов за лучший фильм. [br]', 'poster_zelenaia_milia_1999.jpg', '', 'Films014', 'драма, мистика', 'США'),
(45, 'Interstate 60', 'Трасса 60', '2002', 'Боб Гэйл / Bob Gale', 'Джеймс Марсден, Гэри Олдмэн, Кристофер Ллойд, Курт Расселл, Эми Смарт, Майкл Дж. Фокс', 'В легендах и мифах есть персонажи, главная задача которых – исполнять желания. У арабов – это джинны, у ирландцев – лепреконы, у китайцев – драконы, у европейцев – феи и лесные духи. А в Америке есть некто О. Ж. Грант – довольно странный и забавный человек, который тоже может исполнить любое Ваше желание. Но будьте осторожны – он очень проказлив! Вот его-то и встречает однажды Нил Оливер. Нил вполне доволен своей жизнью: у него обеспеченные родители, симпатичная невеста и впереди блестящая карьера юриста. Но с недавних пор по ночам ему стала сниться загадочная девушка, которую он никак не может выбросить из головы. Чудаковатый Грант приглашает Нила совершить поездку по таинственной автостраде 60, которой нет ни на одной карте США. И Нил бросает все и пускается в самое невероятное и рискованное путешествие в своей жизни, решив, во что бы то ни стало разыскать незнакомку из своих снов. ', 'poster_Trassa_60_2002.jpg', '01:51:55', 'Films014', 'Комедия, Приключения', 'США'),
(46, 'Stay Alive', 'Остаться в живых', '2006', 'Уильям Брент Белл', 'Джон Фостер, Самэр Армстронг, Фрэнки Муниц, Джимми Симпсон, Уэнделл Пирс, Мило Вентимиглиа, София Буш, Адам Голдберг, Билли Слоутер, Николь Опперманн и др.\r\n', 'Группа подростков, увлеченных игрой в компьютерный хоррор не замечают, как виртуальная реальность постепенно вторгается в их жизнь. По мере того, как их компьютерные прототипы погибают от рук виртуальной нечести, жизни самих игроков начинает угрожать вполне реальная опасность – неизвестная сила начинает их убивать теми же способами, что и в самой игре. ', 'poster_Ostatsia_v_gzivyh_2006.jpg', '01:24:19', 'Films014', 'ужасы, триллер', 'США'),
(47, 'The Da Vinci Code', 'Код Да Винчи', '2006', 'Рон Ховард / Ron Howard', 'Том Хэнкс, Одри Тоту, Иэн МакКеллен, Жан Рено, Пол Беттани, Альфред Молина, Юрген Прохнов, Жан-Ив Бертело, Этьен Шико, Жан-Пьер Мариэль', 'Гарвардского профессора Роберта Лэнгдона подозревают в чудовищном преступлении, которого он не совершал.\r\nЛэнгдон знакомится с криптографом парижской полиции Софи Нево и вместе с ней пытается раскрыть тайну, которая может подорвать могущество католической церкви.\r\nРоберт и Софи должны это сделать до того, как их схватят блюстители закона или остановят противники, готовые на все ради сохранения великого религиозного секрета… ', 'poster_Kod_da_vinchi_2006.jpg', '02:54:37', 'Films014', 'триллер, драма, детектив', 'США'),
(48, 'Hackers &quot;Their only crime was curiosity.&quot;', 'Хакеры', '1995', 'Айэйн Софтли / Iain Softley', 'Джонни Ли Миллер /Jonny Lee Miller/, Анджелина Джоли /Angelina Jolie/,\r\nФишер Стивенс /Fisher Stevens/, Джесси Брэдфорд /Jesse Bradford/, Мэттью Лиллард /Matthew Lillard/,\r\nЛоуренс Мэйсон /Laurence Mason/, Реноли Сантьяго /Renoly Santiago/, Уэнделл Пирс /Wendell Pierce/,\r\nУотсон Альберта /Alberta Watson/, Лоррэйн Бракко /Lorraine Bracco/', 'Развлекаясь манипуляциями в коммерческих сетях, начинающий хакер делает почти невозможное: взламывает защиту секретного компьютера Гибсон в корпорации Эллингсон. При этом он случайно подключается к схеме хищения средств, искусно замаскированной кем-то под компьютерный вирус, действие которого может привести к глобальной экологической катастрофе.[br]\r\nМолодой хакер и его приятели в доли секунды превращаются в подозреваемых. Охоту за ними начинает ФБР и главный компьютерщик корпорации Эллингтон по кличке Чума (Фишер Стивенс). Однако молодые хакеры раскрывают губительность истинных планов Чумы и объединяют свои\r\nтехнические знания, чтобы запустить массированную киберпространственную атаку, которая должна\r\nочистить их от подозрений и предотвратить экологическую катастрофу. [br]', 'poster_hackers_1995.jpg', '01:45:11', 'Films014', 'боевик, триллер, криминальный', 'США / USA'),
(49, 'Double zero', 'Два нуля', '2004', 'Жерар Пирес', 'Эдуард Баэр, Эрик Жюдор, Рамзи Бедиа.', 'Бен и Вилл - самые бездарные агенты, полные нули. Кому же еще поручить суперсложное, сверхсекретное задание? Русские купили у французов новейшую супермощную ракету М51, за бешеные деньги. Не успели они обмыть покупку, как ракету похитили... Обезвредить похитителя сможет далеко не каждый дурак, а только наши безбашенные друзья. Их ждет незабываемый вояж из Парижа на сказочную Ямайку, прямо в логово врага. Тупиц ничто не остановит. Им все нипочем - ни танки, ни армия ниндзя, ни команда обнаженных топ-моделей. У чудаков своя логика. Они спасут мир... ', 'poster_Dva_nulia_2004.jpg', '90 мин.', 'Films014', 'Комедия, Приключения, Художественный', 'Франция'),
(50, 'Eva', 'Ева', '2009', 'Адриан Попович', 'Ванесса Редгрейв, Майкл Айронсайд, Винсент Риган, Патрик Берджин, Дастин Миллиган', 'Ева встретила Тюдора в свой 16-й день рождения и полюбила его на всю жизнь. Но на их отношения влияли постоянные загадочные исчезновения молодого человека. Во время одного из таких исчезновений Ева выходит замуж за барона Освальда Фон Зееле. Но когда Тюдор вновь возвращается, чувства между ним и Евой вспыхивают с новой силой. Они проводят вместе незабывамое лето. Ева забеременела, ее брак разрушен. Тюдор снова исчезает, а в Европе начинается война... ', 'poster_Eva_2009.JPG', '01:42:16', 'Films015', 'драма', 'Румыния'),
(51, 'Cop Out', 'Двойной КОПец', '2010', 'Кевин Смит', 'Брюс Уиллис, Трэйси Морган, Адам Броуди, Шон Уильям Скотт, Мишель Трахтенберг, Джейсон Ли, Кевин Поллак, Марк Консуэлос, Гильермо Диаз, Эдриэн Мартинес', 'История двух давних напарников — нью-йоркских полицейских, занятых поисками очень редкой и чертовски дорогой бейсбольной карточки, украденной у одного из них. Они вынуждены противостоять беспощадному гангстеру, просто помешанному на коллекционных вещицах.', 'poster_Dvojnoj_kopetz_2010.JPG', '01:42:47', 'Films015', 'боевик, комедия, криминал', 'США, Warner Bros. Pictures'),
(52, 'Alice in Wonderland', 'Алиса в стране чудес «Вы приглашены на безумно важное событие»', '2010', 'Тим Бёртон', 'Миа Васиковска, Джонни Депп, Хелена Бонем Картер, Энн Хэтэуэй, Криспин Гловер, Мэтт Лукас, Стивен Фрай, Майкл Шин, Алан Рикман, Барбара Уиндсор', 'Жизнь 19-летней Алисы Кингсли принимает неожиданный оборот. На викторианской вечеринке, устроенной в её честь, Алисе делает предложение Хэмиш, богатый, но глупый сын лорда и леди Эскот. Не дав ответа, девушка убегает и идёт за кроликом, замеченным ею на лужайке. Кролик как кролик, вот только он одет в камзол и всё время смотрит на карманные часы.[br]\r\nАлиса гонится за Белым Кроликом через весь луг и видит, как он исчезает в кроличьей норе. Затем, неожиданно, Алиса падает туда сама, летит вниз по странному, сказочному туннелю и приземляется в круглом зале со множеством дверей. Выпив из бутылки с надписью «ВЫПЕЙ МЕНЯ», она уменьшается в размерах, а откусив от пирожного с надписью «СЪЕШЬ МЕНЯ» — вырастает. Наконец, миновав череду злоключений, Алиса попадает через одну из дверей в чудесный фантастический мир, который его обитатели называют Подземной страной.[br]\r\nТам её ждёт целый парад колоритных персонажей: хвастливая Соня, сумасшедший Шляпник, глумливый Чеширский Кот, накурившаяся кальяном гусеница по имени Абессалом, жутковатая Белая Королева и её злобная сестра, Королева Червей, вздорная и раздражительная правительница Подземной страны.', 'poster_Alisa_v_strane_chudes_2010.JPG', '01:49:02', 'Films015', 'фэнтези, приключения, семейный', 'США / Walt Disney Pictures'),
(53, 'CLASH OF THE TITANS', 'Битва Титанов', '2010', 'Луи Летерье', 'Сэм Уортингтон, Лиам Нисон, Рэйф Файнс, Джейсон Флеминг, Джемма Артертон, Алекса Давалос, Тине Штапельфелдт, Мадс Миккельсен, Люк Эванс, Изабелла Мико', 'Персей, сын бога, воспитанный человеком, не смог защитить свою семью от Аида, мстительного повелителя подземного мира. Теперь ему уже нечего терять, и он добровольно соглашается возглавить опасную миссию, чтобы одержать победу над Аидом до того, как тот отберет власть у Зевса и выпустит демонов подземного царства на Землю.\r\nВозглавляя отряд бесстрашных воинов, Персей отправляется в рискованное путешествие по лабиринту запретных миров. Чтобы победить в свирепой схватке с жуткими демонами и злобными чудовищами, противостоять злому року и стать хозяином своей судьбы, он должен осознать и принять свою божественную силу. ', 'poster_Bitva_Titanov_2010.jpg', '01:32:59', 'Films015', 'боевик, фэнтези, драма, приключения', 'США, Великобритания, Warner Bros. Pictures'),
(54, 'Завтра была война', 'Завтра была война', '1987', 'Юрий Кара', 'Сергей Никоненко, Нина Русланова, Вера Алентова, Владимир Заманский, Ирина Чериченко, Наталья Негода, Юлия Тархова, Радий Овчинников, Геннадий Фролов, Владислав Демченко, Сергей Столяров, Виктор Аникиев, Екатерина Никоненко, Владимир Маслаков, Александр Гаврилов, Михаил Анисимов, Александр Александров, Елизавета Потапова, Екатерина Воронина, Ким Староскольцев, Лина Гурина, Миша Николаев', 'Маленький провинциальный город. 1940 год, отмеченный печатью сталинских репрессий. Танго "Утомленное солнце", великие стройки пятилеток... Люди живут мечтою о прекрасном будущем, но врывается сюда страшное настоящее, "черные маруси" - машины, которые увозили как врагов народа арестованных по доносам отцов и матерей. Семья репрессированного автоматически выключалась из жизни общества, и ее члены должны были сделать свой выбор: либо отречься от него, либо пережить свой круг ада страданий и унижений. Так, в одну темную осеннюю ночь увезли отца Вики Люберецкой, героя гражданской войны, директора авиазавода. История Вики, трагедия ее семьи, поставила шестнадцатилетних мальчишек и девчонок перед необходимостью сделать тот нравственный выбор, который диктовала им совесть. В школе с подачи учительницы начинается травля девочки, ее заставляют отречься от отца публично, и она кончает жизнь самоубийством. Отца же ее вскоре выпускают - сталинская мясорубка почему-то дала сбой. А завтра – война, и большинству школьников, Викиных одноклассников, суждено погибнуть в ее огне...', 'Films016_poster_Zavtra_byla_vojna_1987.jpg', '01:24:29', 'Films016', 'драма, военный', 'CCCP'),
(55, 'Три плюс Два', 'Три плюс Два', '1963', 'Генрих Оганисян', 'Н.Кустинская Н.Фатеева А.Миронов Е.Жариков Г.Нилов', 'Какой, оказывается, веселой и беззаботной была жизнь еще недавно. Две девушки могли запросто махнуть на машине на юг, причем без всякой охраны. А трое молодых людей, оказавшиеся их соседями на диком черноморском пляже, не только не совершают ничего предосудительного, но сами становятся их жертвами... Разумеется, жертвами любви. Забавные приключения легкомысленных героев фильма - это сегодня как глоток свежего морского воздуха, запах которого многие стали забывать...', 'Films016_poster_Tri_plus_dva_1963.jpg', '1:23:38 ', 'Films016', 'комедия', 'СССР Киностудия им.Горького и Рижская Киностудия'),
(56, 'Naked Science: Birth Of The Universe', 'С точки зрения науки. Дело о планете Земля. Рождение Вселенной.', '2006', 'Stephen Marsh', '', '"С точки зрения науки" — это захватывающий сериал, который расскажет вам осе о самых популярных у актуальных научных темах нашего времени. Каждый эпизод, основываясь на последних достижениях научно-технического прогресса о мнениях передовых исследователей, доступным языком расскажет величайших загадкам нашей планете и за ее пределами. Все от исторических фактов до сверхъестественных событий, от природных аномалий до глобальных катастроф. Вы сможете не просто получить развернутые ответы ученых, но и с помощью современных спецэффектов как бы оказаться па месте событии. Добро пожаловать в мир знаний и удивительных открытий![br]\r\n[br]\r\nОригинальное название Naked Science: Birth Of The Universe\r\nВы когда-нибудь интересовались происхождением воды в вашем стакане? Основной элемент воды водород, составляющий ее большую часть, возник во время Большого взрыва. Его можно обнаружить и в звездах, и в галактиках, и в чашке кофе. [br]', 'Films016_poster_s_tochki_zreniya_nauki_Delo_o_planete_zemlya_Rozhdenie_vselennoi.jpg', '00:46:54', 'Films016', 'Документальный сериал', 'США / USA  Pioneer Productions (for National Geographic Channel)'),
(57, 'НЛО уходит под воду', 'НЛО уходит под воду', '2006', 'Виталий Правдивцев', '', 'Неопознанные летающие объекты. Исследуя этот феномен, мы часто не задумываемся,\r\nчто три четверти земной поверхности занимают морские просторы - места, мало населенные -\r\nоткуда сообщения об этих явлениях поступают довольно редко. А, между тем, НЛО явно тяготеют\r\nк водной стихии...\r\nО событиях, связанных с изучением неопознанных летающих объектов в Военно-морском флоте, о\r\nсвоих встречах с этими явлениями расскажут командиры атомных подводных лодок. Ветераны\r\nразведки ВМФ, исследователи аномалий в гидросфере. Об организации сбора информации о НЛО\r\nрасскажет бывший главнокомандующий ВМФ СССР адмирал флота В.Н. Чернавин. ', 'Films016_poster_nlo_uhodit_pod_vodu.jpg', '44:06', 'Films016', 'Документальный', 'Россия Рен TV'),
(58, 'Город,которого больше нет (Припять)', 'Город,которого больше нет (Припять)', '2006', '', '\r\n', 'К майским праздикам было уже все готово описинные лозунгами витрины, сценарии демонстраций уже были отданы на утверждение начальства, в ресторанах составляли праздничное меню. Все были в томительном ожидании 3 выходных. Мечты о праздники рухнули 26 апреля.....', 'Films016_poster_gorod_kotorogo_bolshe_net_KRT.jpg', '00:19:36', 'Films016', 'Документальный', 'Украина'),
(59, 'Ефимов: Культура мышления', 'Ефимов: Культура мышления', '2001', '', '', 'Видеолекция "Развитие миропонимания культуры мышления" затрагивает следующие вопросы: значение и взаимосвязь сознания и безсознательных уровней психики, иерархия уровней управления, типовая схема сокрытия истины и др. Материал дается с позиций концепции общественной безопасности. \r\n', 'Films016_poster_Efimov_Kultura_myshleniya.jpg', '2 ч 7 мин 57 сек', 'Films016', 'публицистика', 'Россия'),
(62, 'The Lord of the Rings: Two Towers', 'Властелин Колец: Две Башни (Режиссерская версия) ', '2002', 'Питер Джексон /Peter Jackson/', 'Элайджа Вуд /Elijah Wood/, Йан МакКеллен /Ian McKellen/, Вигго Мортенсон /Viggo Mortensen/, Орландо Блум /Orlando Bloom/, Шон Остин /Sean Astin/, Доминик Монэхэн /Dominic Monaghan/, Билли Бойд /Billy Boyd/, Джон Рис-Дэвис /John Rhys-Davies/, Кейт Бланшетт /Cate Blanchett/, Лив Тайлер /Liv Tyler/, Кристофер Ли /Christopher Lee/', '\r\n"Властелин Колец: Две башни" является продолжением эпического похода Фродо Бэггинса, который должен уничтожить Кольцо Власти, хранящее силу Черного Властелина Саурона. Братство Кольца распалось, и в то время, как Фродо и Сэм с помощью неожиданного и опасного союзника пробираются к Мордору, их друзья, пройдя через многие опасности и приключения, вступают в решающий бой с магом-предателем Саруманом.\r\n\r\nПолная режиссерская версия с добавленными 42 минутами совершенно новых и удлиненных сцен. Эта изумительная картина была представлена на шесть премий американской киноакадемии "Оскар", включая награду за "Фильм Года". \r\n', 'poster_The_Lord_of_the_Rings_Two_Towers_2002.png', '03:44:07', 'Films018', 'Фэнтези, сказка, приключения, драма', 'США , Новая Зеландия'),
(61, 'The Lord of the Rings: The Fellowship of the Ring', 'Властелин колец: Братство кольца (Режиссерская версия) ', '2001', 'Питер Джексон', 'Elijah Wood (as Frodo Baggins), Ian McKellen (as Gandalf the Grey/Gandalf the White), Liv Tyler (as Arwen), Viggo Mortensen (as Aragorn), Sean Astin (as Samwise ''Sam'' Gamgee), Cate Blanchett (as Galadriel), John Rhys-Davies (as Gimli/Voice of Treebeard), Bernard Hill (as Theoden), Christopher Lee (as Saruman the White), Billy Boyd (as Peregrin ''Pippin'' Took), Dominic Monaghan (as Meriadoc ''Merry'' Brandybuck), Orlando Bloom (as Legolas Greenleaf), Hugo Weaving (as Elrond), Miranda Otto (as Eowyn), David Wenham (as Faramir)', 'Сказания о Средиземье - это хроника Великой войны за Кольцо, войны, длившейся не одну тысячу лет. Тот, кто владел Кольцом, получал власть над всеми живыми тварями, но был обязан служить злу. Тихая деревня, где живут хоббиты.\r\n\r\nВолшебник Гэндальф, придя на 111-й день рождения к своему старому другу Бильбо Бэггинсу, начинает вести разговор о кольце, которое Бильбо нашел много лет назад. Это кольцо принадлежало когда-то темному властителю Средиземья Саурону, и оно дает большую власть своему обладателю.\r\n\r\nТеперь Саурон хочет вернуть себе власть над Средиземьем. Бильбо отдает найденное кольцо своему племяннику Фродо, который пытается научиться справляться с тем страшным могуществом, которое дает ему кольцо…\r\n\r\n', 'poster_The_Lord_of_the_Rings_The_Fellowship_of_the_Ring_2001.jpg', '03:28:18', 'Films017', 'фэнтези, приключения, драма', 'США, Новая Зеландия'),
(63, 'Lord of the Rings: Return of the King SEE', 'Властелин Колец: Возвращение Короля (Режиссерская версия) ', '2004', 'Питер Джексон', 'Элайджа Вуд /Elijah Wood/, Иен МакКеллен /Ian McKellen/, Орландо Блум /Orlando Bloom/, Вигго Мортенсен /Viggo Mortensen/, Шон Эстин /Sean Astin/, Билли Бойд /Billy Boyd/', 'Последняя часть трилогии о Кольце Всевластия и о героях, взявших на себя бремя спасения Средиземья.\r\n\r\nПовелитель сил Тьмы Саурон направляет свои бесчисленные рати под стены Минас-Тирита, крепости Последней Надежды.Он предвкушает близкую победу, но именно это и мешает ему заметить две крохотные фигурки - хоббитов,приближающихся к Роковой Горе, где им предстоит уничтожить Кольцо Всевластия. Улыбнется ли им счастье?..Настало время Арагорну стать во главе Гондора, наследником престола которого он всегда был. Ибо время не ждет.Грядет сражение с армией Саурона, исход которого решит судьбу всего человечества.\r\n\r\n', 'poster_The_Lord_of_the_Rings_Return_of_the_King_2004.jpg', '04:12:27', 'Films019', 'фэнтези, драма, приключения', 'США- Новая Зеландия'),
(64, ' Allan Quatermain and the Temple of Skulls', 'Аллан Куотермейн и Храм Черепов ', '2008', 'Марк Аткинс', 'Шон Майкл, Кристофер Адамсон, Натали Стоун, Дэниэл Бонжур, Витли Журдан', ' Самое большое историческое сокровище на самой опасной земле в мире...\r\n\r\nСнято в Южной Африке, по роману H. Rider Haggard "Копи Царя Соломона", показывая авантюриста, который был вдохновением для Indiana Jones.\r\n\r\n', 'poster_Alan_Kvantermejn_i_Hram_Cherepa_2008.jpg', '01:37:57.204 (140912 frames)', 'Films020', 'Боевик', 'США, The Asylum');
